mapper.select:
select count(*)
  from frequent_approval_line
 where emp_id = #{empId} and line_name = #{lineName}

select count(*) from (select v.doc_no, e.emp_name, v.doc_title, v.doc_sc, v.doc_date, a.approver_empid, a.approve_status from vacation v join approve_line a on(v.doc_no = a.doc_no) join employee e on(v.emp_id = e.emp_id) UNION ALL select f.doc_no, e.emp_name, f.doc_title, f.doc_sc, f.doc_date, a.approver_empid, a.approve_status from family_event f join approve_line a on(f.doc_no = a.doc_no) join employee e on(f.emp_id = e.emp_id) UNION ALL select p.doc_no, e.emp_name, p.doc_title, p.doc_sc, p.doc_date, a.approver_empid, a.approve_status from proof p join approve_line a on(p.doc_no = a.doc_no) join employee e on(p.emp_id = e.emp_id) UNION ALL select d.doc_no, e.emp_name, d.doc_title, d.doc_sc, d.doc_date, a.approver_empid, a.approve_status from draft d join approve_line a on(d.doc_no = a.doc_no) join employee e on(d.emp_id = e.emp_id)) where approver_empid=#{empId} and approve_status=#{approveStatus}
and emp_name like '%' || #{writer} || '%'
and doc_title like '%' || #{title} || '%'
and doc_sc like '%' || #{form} || '%'
and doc_date between #{startDate} and #{endDate}

select * from (select * from (select v.doc_no, v.doc_sc, v.doc_title, v.emp_id, e.emp_name, v.doc_department, v.doc_date, a.approver_empid, a.approve_status from vacation v join approve_line a on(v.doc_no = a.doc_no) join employee e on(v.emp_id = e.emp_id) order by v.doc_date desc) UNION ALL select * from (select f.doc_no, f.doc_sc, f.doc_title, f.emp_id, e.emp_name, f.doc_department, f.doc_date, a.approver_empid, a.approve_status from family_event f join approve_line a on(f.doc_no = a.doc_no) join employee e on(f.emp_id = e.emp_id) order by f.doc_date desc) UNION ALL select * from (select d.doc_no, d.doc_sc, d.doc_title, d.emp_id, e.emp_name, d.doc_department, d.doc_date, a.approver_empid, a.approve_status from draft d join approve_line a on(d.doc_no = a.doc_no) join employee e on(d.emp_id = e.emp_id) order by d.doc_date desc) UNION ALL select * from (select p.doc_no, p.doc_sc, p.doc_title, p.emp_id, e.emp_name, p.doc_department, p.doc_date, a.approver_empid, a.approve_status from proof p join approve_line a on(p.doc_no = a.doc_no) join employee e on(p.emp_id = e.emp_id) order by p.doc_date desc) ) where approver_empid=#{empId} and approve_status=#{approveStatus}
and emp_name like '%' || #{writer} || '%'
and doc_title like '%' || #{title} || '%'
and doc_sc like '%' || #{form} || '%'
and doc_date between #{startDate} and #{endDate}
order by doc_date desc

select count(approve_comment)
from approve_line
where doc_no=#{docNo}

select approve_status
from approve_line
where doc_no=#{docNo} and approver_empid=#{approverEmpid}

mapper.update:
update approve_line
   set approve_reject='진행'
 where doc_no=#{docNo} and approver_empid=#{approverEmpid}

update approve_line
   set approve_status=2
 where doc_no=#{docNo} and approver_empid=#{approverEmpid}

update approve_line
   set approve_status=3
 where doc_no=#{docNo} and approver_empid=#{approverEmpid}
