mapper.select:
select count(*) from v_board_list where bd_status = 'Y' and bd_category = #{boardCategory}
and emp_name like '%'||#{writer}||'%'
and bd_title like '%'||#{title}||'%'
and bd_content like '%'||#{content}||'%'
and create_date between #{start} and #{end}

select * from v_board_list where bd_status = 'Y' and bd_category = #{boardCategory}
and emp_name like '%'||#{writer}||'%'
and bd_title like '%'||#{title}||'%'
and bd_content like '%'||#{content}||'%'
and create_date between #{start} and #{end}
order by board_sq desc

mapper.update:
update board
   set bd_status ='N'
 where board_sq = #{bno}

update board_attachment
   set status = 'N'
 where bd_file_no = #{bcFileNo}

mapper.select:
select * 
  from board_like
 where emp_id = #{empId} and board_sq = #{boardNo}
