mapper.update:
update employee
   set origin_name = #{originName},
       change_name = #{changeName}
 where emp_id=#{empId}

update employee
   set emp_pwd=#{empPwd}
 where emp_id=#{empId}

mapper.select:
	select *
	  FROM ATTENDANCE
     WHERE EMP_ID=#{empId}
       and att_date between #{startDate} and #{endDate}
     order by att_date desc
