mapper.update:
 			update email_address
		       set read_status='Y' 
             where receiver =#{recipients} and email_no=#{emailNo}

update email_address
   set status='N' 
 where receiver =#{recipients} and email_no=#{emailNo}

update email
set email_status ='N'
where sender=#{sender} and email_no=#{emailNo}

update.if:
update email
set email_status ='Y'
where sender=#{empId} and email_no=#{emailNo}

	update email_address
       set status='Y' 
	 where receiver =#{empId} and email_no=#{emailNo}
