mapper.select:
SELECT *
  FROM SCHEDULE
 WHERE SCH_DEL_STATUS='N' AND EMP_ID=#{empId}

SELECT *
  FROM SCHEDULE
 WHERE SCH_DEL_STATUS='N' AND SCH_NO=#{schNo}

mapper.update:
UPDATE SCHEDULE
   SET SCH_TITLE=#{schTitle}, SCH_CONTENT=#{schContent}, SCH_STR_DATE=#{startDate}, SCH_END_DATE=#{endDate}
 WHERE SCH_NO=#{schNo}

UPDATE SCHEDULE
   SET SCH_DEL_STATUS='Y'
 WHERE SCH_NO=#{schNo}
